function setupButtons(){document.getElementById("artsubmit").addEventListener("click",(function(e){e.preventDefault();var t=getFormData();t.djimage?convertImageToBase64(t.djimage):postFormToServer(t,!1)})),document.getElementById("base64-art").addEventListener("click",(function(e){return e.preventDefault(),!1})),document.getElementById("copy-base64").addEventListener("click",(function(e){e.preventDefault(),copyToClipboard(),M.toast({html:"Image Base64 copied to clipboard"})}))}function copyToClipboard(){var e=document.getElementById("base64-art");e.select(),e.setSelectionRange(0,99999),navigator.clipboard.writeText(e.value)}function convertImageToBase64(e){var t=document.getElementById("converter-img");if(e.size>0){var n=new FileReader;n.onload=function(e){t.src=e.target.result,postFormToServer(getFormData(),!0)},n.readAsDataURL(e)}}function getFormData(){var e=document.getElementById("djcreds"),t=new FormData(e);return Object.fromEntries(t)}function postFormToServer(e,t){let n=window.XMLHttpRequest?new XMLHttpRequest:new ActiveXObject("Microsoft.XMLHTTP");n.onreadystatechange=function(){if(4===this.readyState&&200===this.status&&this.responseText){var e=document.getElementById("art-preview"),t=JSON.parse(this.responseText);e.src=t.img,document.getElementById("base64-art").value=t.img}};var o="";t&&(o=document.getElementById("converter-img").src);var a="payload="+encodeURIComponent(JSON.stringify({id:e.templateid,text1:encodeURIComponent(e.text1),text2:encodeURIComponent(e.text2),text3:encodeURIComponent(e.text3),text4:encodeURIComponent(e.text4),img:encodeURIComponent(o)}));n.open("POST","/",!0),n.setRequestHeader("Content-type","application/x-www-form-urlencoded"),n.send(a)}document.addEventListener("DOMContentLoaded",(function(){setupButtons()}));